<?php defined('IN_XQCMS') or exit('Access Denied');class flink {public $id;private $db;private $table;public function flink() {global $db, $XQ_PRE;$this->table = $XQ_PRE.'link';$this->db = &$db;}private function public_check($post) {global $L, $XQ_TIME;if(!is_array($post)) msg();if(!$post['tid']) msg($L['link_pass_type']);if(!$post['title']) msg($L['link_pass_site']);if(!$post['url']) msg($L['link_pass_url']);if(!$this->id) $post['addtime'] = $XQ_TIME;$post['edittime'] = $XQ_TIME;return $post;}public function fone() {return $this->db->get_one("SELECT * FROM {$this->table} WHERE id='$this->id'");}public function flist($condition = '1', $order = 'listorder DESC, id DESC') {global $MOD, $TYPE, $pages, $page, $pagesize, $offset;$r = $this->db->get_one("SELECT COUNT(*) AS num FROM {$this->table} WHERE $condition");$pages = pages($r['num'], $page, $pagesize);$lists = array();$result = $this->db->query("SELECT * FROM {$this->table} WHERE $condition ORDER BY $order LIMIT $offset,$pagesize");while($r = $this->db->fetch_array($result)) {$r['title'] = set_style($r['title'], $r['color']);$r['adddate'] = timetodate($r['addtime'], 5);$r['editdate'] = timetodate($r['edittime'], 5);$r['name'] = $TYPE[$r['tid']]['name'];$lists[] = $r;}return $lists;}public function add($post) {$post = $this->public_check($post);$sqln = $sqlv = '';foreach($post as $n=>$v) {$sqln .= ','.$n; $sqlv .= ",'$v'";}$sqln = substr($sqln, 1);$sqlv = substr($sqlv, 1);$this->db->query("INSERT INTO {$this->table} ($sqln) VALUES ($sqlv)");$this->id = $this->db->insert_id();return $this->id;}public function edit($post) {$post = $this->public_check($post);$sql = '';foreach($post as $n=>$v) {$sql .= ",$n='$v'";}$sql = substr($sql, 1);$this->db->query("UPDATE {$this->table} SET $sql WHERE id=$this->id");return true;}public function delete($id, $all = true) {if(is_array($id)) {foreach($id as $v) {$this->delete($v, $all);}} else {$this->id = $id;$r = $this->fone();if(!$r) msg('');if($all) $this->db->query("DELETE FROM {$this->table} WHERE id=$id");}}public function check($id) {global $XQ_TIME;if(is_array($id)) {foreach($id as $v) { $this->check($v); }} else {$this->db->query("UPDATE {$this->table} SET status=3,edittime=$XQ_TIME WHERE id=$id");return true;}}public function order($listorder) {if(!is_array($listorder)) return false;foreach($listorder as $k=>$v) {$k = intval($k);$v = intval($v);$this->db->query("UPDATE {$this->table} SET listorder=$v WHERE id=$k");}return true;}}?>