<?php defined('IN_XQCMS') or exit('Access Denied');class products {private $moduleid;public $id;private $db;private $table;public function products($moduleid) {global $db, $table;$this->moduleid = $moduleid;$this->table = $table;$this->db = &$db;}private function public_check($post) {global $MOD, $XQ_TIME, $_username;if(!is_array($post)) msg();if(!$post['catid']) msg(lang('message->pass_catid'));if(strlen($post['title']) < 3) msg(lang('message->pass_title'));if(!$post['content']) msg(lang('message->pass_content'));$post['posid']=@implode(",",$post['posid']);$post['addtime'] = (isset($post['addtime']) && $post['addtime']) ? strtotime($post['addtime']) : $XQ_TIME;$post['edittime'] = $XQ_TIME;$post['title'] = trim($post['title']);$post['content'] = stripslashes($post['content']);if($MOD['clear_link']) $post['content'] = clear_link($post['content']);if($MOD['save_remotepic']) $post['content'] = save_remote($post['content']);if($MOD['introduce_length']&&!$post['introduce']) $post['introduce'] = addslashes(get_intro($post['content'], $MOD['introduce_length']));$post['keyword'] = $post['keyword']? trim($post['keyword']):$post['title'];if($this->id) {$post['editor'] = $_username;unset($post['username']);} else {$post['username'] = $post['editor'] = $_username;}unset($post['clear_link']);unset($post['introduce_length']);unset($post['save_remotepic']);$post['content'] = addslashes($post['content']);return $post;}public function fone() {return $this->db->get_one("SELECT * FROM {$this->table} WHERE id=$this->id");}public function flist($condition = 'status=3', $order = 'edittime DESC', $cache = '') {global $MOD, $pages, $page, $pagesize, $offset, $items;$r = $this->db->get_one("SELECT COUNT(*) AS num FROM {$this->table} WHERE $condition", $cache);$items = $r['num'];$pages = defined('CATID') ? listpages(1, CATID, $items, $page, $pagesize, 10, $MOD['url']) : pages($items, $page, $pagesize);$lists = array();$result = $this->db->query("SELECT * FROM {$this->table} WHERE $condition ORDER BY $order LIMIT $offset,$pagesize", $cache);while($r = $this->db->fetch_array($result)) {$r['alt'] = $r['title'];$r['title'] = set_style($r['title'], $r['color']);$r['url'] = $MOD['url'].$r['url'];$lists[] = $r;}return $lists;}public function add($post) {$post = $this->public_check($post);$sqln = $sqlv = '';foreach($post as $n=>$v) {$sqln .= ','.$n; $sqlv .= ",'$v'";}$sqln = substr($sqln, 1);$sqlv = substr($sqlv, 1);$this->db->query("INSERT INTO {$this->table} ($sqln) VALUES ($sqlv)");$this->id = $this->db->insert_id();$this->update($this->id, $post);if($post['status'] > 2) $this->create_html($this->id, $post['catid']);return $this->id;}public function edit($post) {$this->delete($this->id, false);$post = $this->public_check($post);$sql = '';foreach($post as $n=>$v) {$sql .= ",$n='$v'";}$sql = substr($sql, 1);$this->db->query("UPDATE {$this->table} SET $sql WHERE id=$this->id");$this->update($this->id, $post);if($post['status'] > 2) $this->create_html($this->id, $post['catid']);return true;}public function order($listorder) {if(!is_array($listorder)) return false;foreach($listorder as $k=>$v) {$k = intval($k);$v = intval($v);$this->db->query("UPDATE {$this->table} SET listorder=$v WHERE id=$k");}return true;}private function create_html($id = 0, $catid = 0) {global $module, $MOD;if($MOD['show_html'] && $id) tohtml('show', $module, "id=$id");if($MOD['list_html'] && $catid) tohtml('list', $module, "catid=$catid&fid=1&num=3");if($MOD['list_html']) tohtml('list', $module, "catid=0&fid=1&num=3");}public function update($id, $item = array()) {$item or $item = $this->db->get_one("SELECT * FROM {$this->table} WHERE id=$id");$item['id'] = $id;$url = itemurl($item);$this->db->query("UPDATE {$this->table} SET url='$url' WHERE id=$id");}public function recycle($id) {if(is_array($id)) {foreach($id as $v) { $this->recycle($v); }} else {$this->db->query("UPDATE {$this->table} SET status=0 WHERE id=$id");$this->delete($id, false);return true;}}public function restore($id) {global $module, $MOD;if(is_array($id)) {foreach($id as $v) { $this->restore($v); }} else {$this->db->query("UPDATE {$this->table} SET status=3 WHERE id=$id");if($MOD['show_html']) tohtml('show', $module, "id=$id");return true;}}public function delete($id, $all = true) {global $WEB, $MOD;if(is_array($id)) {foreach($id as $v) {$this->delete($v, $all);}} else {$this->id = $id;$r = $this->fone();if(!$r) msg('²ÎÊý´íÎó');if($MOD['show_html']) {$_file = XQ_ROOT.'/'.$MOD['moduledir'].'/'.$r['url'];if(is_file($_file)) unlink($_file);$i = 1;while($i) {$_file = XQ_ROOT.'/'.$MOD['moduledir'].'/'.itemurl($id, $r['catid'], $r['addtime'], $i);if(is_file($_file)) {unlink($_file);$i++;} else {break;}}}if($all) $this->db->query("DELETE FROM {$this->table} WHERE id=$id");}}public function check($id) {global $_username, $XQ_TIME;if(is_array($id)) {foreach($id as $v) { $this->check($v); }} else {$editdate = timetodate($XQ_TIME, 3);$this->db->query("UPDATE {$this->table} SET status=3,editor='$_username',edittime=$XQ_TIME,editdate='$editdate' WHERE id=$id");$this->create_html($id);return true;}}public function clear($condition = 'status=0') {$result = $this->db->query("SELECT id FROM {$this->table} WHERE $condition ");while($r = $this->db->fetch_array($result)) {$this->delete($r['id']);}}public function posid($id, $posid) {$ids = is_array($id) ? implode(',', $id) : $id;$this->db->query("UPDATE {$this->table} SET posid='$posid' WHERE id IN ($ids)");}}?>