<?php require 'version.inc.php';define('XQ_SYSPATH', dirname(__FILE__).DIRECTORY_SEPARATOR);class XQcms {public static function load_xq_class($classname, $path = '') {return self::load_class($classname, $path);}public static function load_models($classname) {return self::load_class($classname,'models');}private static function load_class($classname, $path = '') {if (empty($path)) $path = 'libs'.DIRECTORY_SEPARATOR.'classes';if (file_exists(XQ_SYSPATH.$path.DIRECTORY_SEPARATOR.$classname.'.class.php')) {require XQ_SYSPATH.$path.DIRECTORY_SEPARATOR.$classname.'.class.php';return true;} else {return false;}}public static function load_xq_func($func) {return self::load_func($func);}private static function load_func($func, $path = '') {if (empty($path)) $path = 'libs'.DIRECTORY_SEPARATOR.'functions';$path .= DIRECTORY_SEPARATOR.$func.'.func.php';if (file_exists(XQ_SYSPATH.$path)) include XQ_SYSPATH.$path;return true;}}?>